import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';

import api from '../../services/api';

interface Todo {
  id: string;
  title: string;
  completed: boolean;
  createdAt: string;
  updatedAt: string;
}

const Dashboard: React.FC = () => {

    const [newTodo, setNewTodo] = useState('');
    const [ todos, setTodos] = useState<Todo[]>([]) ;

    useEffect(() => {
      api.get<Todo[]>('/tasks').then(response => {

        setTodos(response.data)
        console.log(todos)
      }); 
    }, [])


    async function handleNewTodo(event: any): Promise<void> {
        if (event.key !== 'Enter') {
          return;
        }
        event.preventDefault();

        const response = await  api.post('/task', {title: event.target.value, completed: false });
        const todo = response.data;

        setTodos([...todos, todo])
        console.log(event.target.value);


    }


    return ( 
        <>
        <header className="todoapp">
          <h1>todos</h1>
          <input
            id="newField"
            className="new-todo"
            placeholder="What needs to be done?"
            onKeyDown = {(e) => handleNewTodo(e)}

            // onKeyDown={ e => (e) }
            // autoFocus={true}
          />
        </header>
        <div>

        <ul >
							{ todos.map( todo => <li key={todo.id}>{todo.title}</li> )}
        </ul>
        </div>
      </>
    )
}


