import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';

import api from '../../services/api';

interface Todo {
  id: string;
  title: string;
  completed: boolean;
  createdAt: string;
  updatedAt: string;
}

const Dashboard: React.FC = () => {


    const [newTodo, setNewTodo] = useState('');
    const [editing, setEditing] = useState('');
    const [ todos, setTodos] = useState<Todo[]>([]) ;

    const activeTodoCount = 0;

    useEffect(() => {
      api.get<Todo[]>('/tasks').then(response => {

        setTodos(response.data)
      }); 
      console.log(todos)
    }, [])

    var hasOwn = {}.hasOwnProperty; 
    function classNames () {
      var classes = '';

      for (var i = 0; i < arguments.length; i++) {
        var arg = arguments[i];
        if (!arg) continue;

        var argType = typeof arg;

        if (argType === 'string' || argType === 'number') {
          classes += ' ' + arg;
        } else if (Array.isArray(arg)) {
          classes += ' ' + classNames.apply(null, arg);
        } else if (argType === 'object') {
          for (var key in arg) {
            if (hasOwn.call(arg, key) && arg[key]) {
              classes += ' ' + key;
            }
          }
        }
      }

      return classes.substr(1);
    }

    if (typeof module !== 'undefined' && module.exports) {
      module.exports = classNames;
    } else if (typeof define === 'function' && typeof define.amd === 'object' && define.amd) {
      // register as 'classnames', consistent with npm package name
      define('classnames', function () {
        return classNames;
      });
    } else {
      window.classNames = classNames;
    }

    async function handleNewTodo(event: any): Promise<void> {
        if (event.key !== 'Enter') {
          return
        }
        event.preventDefault();

        api.post('/tasks', {title: event.target.value, completed: false }).then( response => {
          const todo = response.data;

          setTodos([...todos, todo]);
          console.log(todos);
        });

        setNewTodo("")
        console.log(newTodo);
    }

    function handleCheckChangeTodo(todo: Todo){

      const { id, completed } = todo;

      const todoIndex = todos.findIndex( todo => {
        return todo.id === id
      });


      api.put(`/tasks/${todo.id}`, {id, completed: !completed }).then( response => {
          const todo = response.data;

          setTodos([todos[todoIndex], todo]);
          console.log(todos);
        });
    }

    function onEdit(todo: Todo) {
      setEditing(todo.title)
    }
    return ( 
        <>
        <header className="todoapp">
          <h1>todos</h1>
          <input
            id="newField"
            className="new-todo"
            placeholder="What needs to be done?"
            value = {newTodo}
            onChange={ e => setNewTodo(e.target.value)}
            onKeyDown = {(e) => handleNewTodo(e)}

            // onKeyDown={ e => (e) }
            autoFocus={true}
          />
        </header>
        <section className="main">
						<input
							id="toggle-all"
							className="toggle-all"
							type="checkbox"
							// checked={activeTodoCount === 0}
						/>
						<label
							htmlFor="toggle-all"
						/>
						<ul className="todo-list">
              { todos.map( todo => 
                <li className="todo-list">
                  <div className="view">
                    <input key={todo.id} 
                          className="toggle" 
                          type="checkbox" checked={todo.completed} 
                          onChange={ e => handleCheckChangeTodo(todo)}>

                    </input>
                    <label onDoubleClick={e => setEditing(todo.title)}>
                        {todo.title}
                    </label>
                  </div>
                  <input 
                      // ref="editField"
                      className="edit"
                      // key={todo.id}
                      onDoubleClick={ e => setEditing(todo.title)}
                      value={editing}
                    //   // onBlur={this.handleSubmit}
                      onChange= {e => setEditing(e.target.value)}
                    //   // onKeyDown={e => handleNewTodo(e)}
                  />
                </li>

              )}

						</ul>
					</section>

        {/* <ul >
        </ul> */}

      </>
    )
}


