import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';

import api from '../../services/api';

interface Todo {
  id: string;
  title: string;
  completed: boolean;
  createdAt: string;
  updatedAt: string;
}

const Dashboard: React.FC = () => {

    const [newTodo, setNewTodo] = useState('');
    const [ todos, setTodos] = useState<Todo[]>([]) ;

    const activeTodoCount = 0;

    useEffect(() => {
      api.get<Todo[]>('/tasks').then(response => {

        setTodos(response.data)
      }); 
      console.log(todos)
    }, [])

    async function handleNewTodo(event: any): Promise<void> {
        if (event.key !== 'Enter') {
          return;
        }
        event.preventDefault();

        // api.post('/tasks', {title: event.target.value, completed: false }).then( response => {
        //   const todo = response.data;

        //   setTodos([...todos, todo]);
        //   console.log(todos);
        // });

        setNewTodo("")
        console.log(newTodo);
    }

    function handleCheckChangeTodo(todo: Todo){

      const { id, completed } = todo;

      const todoIndex = todos.findIndex( todo => {
        return todo.id === id
      });


      api.put(`/tasks/${todo.id}`, {id, completed: !completed }).then( response => {
          const todo = response.data;

          setTodos([todos[todoIndex], todo]);
          console.log(todos);
        });
    }


    return ( 
        <>
        <header className="todoapp">
          <h1>todos</h1>
          <input
            id="newField"
            className="new-todo"
            placeholder="What needs to be done?"
            value = {newTodo}
            onChange={ e => setNewTodo(e.target.value)}
            onKeyDown = {(e) => handleNewTodo(e)}

            // onKeyDown={ e => (e) }
            autoFocus={true}
          />
        </header>
        <section className="main">
						<input
							id="toggle-all"
							className="toggle-all"
							type="checkbox"
							// checked={activeTodoCount === 0}
						/>
						<label
							htmlFor="toggle-all"
						/>
						<ul className="todo-list">
              { todos.map( todo => 
                <div className="view">
                  <input key={todo.id} className="toggle" type="checkbox" checked={todo.completed} onChange={ e => handleCheckChangeTodo(todo)}></input>
                  {/* <button type="button" onClick={handleRemoveTodo} /> */}
                  <input
                  //   // ref="editField"
                  //   className="edit"
                    value={todo.title}
                  //   // onBlur={this.handleSubmit}
                    onChange= {e => setNewTodo}
                  //   // onKeyDown={e => handleNewTodo(e)}
                  /> 
                </div>


              )}

						</ul>
					</section>

        {/* <ul >
        </ul> */}

      </>
    )
}


